package com.chen.leetcode.Seven;import java.util.ArrayList;import java.util.LinkedList;import java.util.Queue;import java.util.Stack;/** * res*10>max-x%10 */public class Solution {    public int reverse(int x) {        int res = 0;        while(x!=0){            if(x>0 && res>(Integer.MAX_VALUE-x%10)/10) return 0;            if(x<0 && res<(Integer.MIN_VALUE-x%10)/10) return 0;            res = res*10 + x%10;            x /=10;        }        return res;    }}